mapper.update:
UPDATE REPORT 
SET REP_STATUS = '경고처리'
WHERE REP_IDX = #{REP_IDX}

UPDATE REPORT 
SET REP_STATUS = '해당없음'
WHERE REP_IDX = #{REP_IDX}

UPDATE MEMBER 
SET MEM_LOCK = 'Y'
WHERE MEM_IDX = #{MEM_IDX}

UPDATE MEMBER 
SET MEM_DEL = '0'
WHERE MEM_IDX = #{MEM_IDX}

UPDATE MEMBER 
SET MEM_DEL = '1', MEM_LOCK = 'N'
WHERE MEM_IDX = #{MEM_IDX}
